var newMap;jQuery(document).ready(function(){newMap=new GoogleMapManager("myCustomMap",{googleMap:{},markers:{}});for(var e=[[45.501526,-73.567853,"My test 1"],[43.501526,-73.567853,"My test 2"],[44.501526,-72.567853,"My test 3"],[44.501526,-72.667853,"My test 4"]],n=0;n<e.length;n++)newMap.addMarker({id:n,title:e[n][2],lat:e[n][0],lng:e[n][1],template:"<div><h1>"+e[n][2]+"</h1></div>",categories:e[n][2]});newMap.on("markerOpened",function(e){this.zoomIn(),this.map.panTo(e.getPosition())}),newMap.on("markerClosed",function(e){this.boundZoom()}),newMap.on("boundZoomed",function(){this.map.panBy(300,0)}),newMap.boundZoom()});